mapper.insert:

	select purchase_seq.nextval from dual

insert into purchase values(#{purchaseIdx}
	,#{itemIdx}
	,#{memberIdx}
	,SYSDATE
	,2)
where item_idx = #{itemIdx}


	select sales_seq.nextval from dual

insert into item values(#{salesIdx}
	,#{itemIdx}
	,SYSDATE
	,2)
where item_idx = #{itemIdx}
