mapper.insert:

	select item_seq.nextval from dual


insert into item values(
	#{itemIdx}
	,#{categoryIdx}
	,#{expertIdx}
	,#{itemName}
	,#{itemContent}
	,#{price}

	,1
	,SYSDATE
	)

mapper.select:
select itemIdx
from item
where expertIdx=#{expertIdx}

mapper.insert:

	select photo_seq.nextval from dual

insert into photo values(
	#{photoIdx}
	,#{itemIdx}
	,#{itemfileName}
	)

mapper.update:
update item set
	photoIdx=PHOTO.photoIdx
where itemIdx=#{itemIdx}
